import { createMuiTheme } from '@material-ui/core/styles'

const theme = createMuiTheme({
    root: {
        width: 345,
    },
        media: {
        height: 140,
    },
    palette: {
        primary: { 500: '#467fcf' },
    },
        typography: {
        fontFamily: [
            'Shrikhand',
            'cursive',
    ]},
        title: {
        color: theme.palette.primary.main
    },
})

export default theme
